DROP DATABASE IF EXISTS 2109013290_blog;
CREATE DATABASE 2109013290_blog;
USE 2109013290_blog;

CREATE TABLE USERS (
	USERNAME VARCHAR(255) PRIMARY KEY NOT NULL,
    ADGANGSORD VARCHAR(255) NOT NULL,
    NAFN VARCHAR(255) NOT NULL
);
CREATE TABLE POSTS (
	ID INT PRIMARY KEY AUTO_INCREMENT,
    DAGSETNING DATETIME,
    LIKES INT,
    TITILL VARCHAR(255) NOT NULL,
    TEXTI LONGTEXT NOT NULL,
    POST_OWNER VARCHAR(255) NOT NULL,
    
    #Aðkomulyklar
    CONSTRAINT USERNAFN_FK FOREIGN KEY (POST_OWNER) REFERENCES USERS(USERNAME)
);
